import Foundation

enum AoCDay6 {
    static let input = """

"""
    static let markerSize = 14
    static func solve() -> Int {
        var index = 0
        let arr = Array(input)
        while index < input.count {
            if isMarker(chars: Array(arr[index..<(index+markerSize)])) {
                print(Array(arr[index..<(index+markerSize)]))
                return index+markerSize
            }
            index += 1
        }

        return 0
    }

    static func isMarker(chars: [Character]) -> Bool {
        chars.count == Set(chars).count
    }
}
